import React from "react";
import {nanoid} from 'nanoid'
import './Game.css'

class ChoiceDifficult extends React.Component{

    diffcultHandle = (event) =>{
        let column;
        switch (event.target){
            case this.easy:
                column = 4
            case this.normal:
                column = 6
            case this.hard:
                column = 8
            default:
                console.log(1)
        }
        this.props.diffcult(event.target.innerText)
    }

    render(){
        return <div className="choiceDifficult">
                    <button ref={current => this.easy = current}   onClick={this.diffcultHandle}>簡單模式</button>
                    <button ref={current => this.normal = current} onClick={this.diffcultHandle}>正常模式</button>
                    <button ref={current => this.hard = current}   onClick={this.diffcultHandle}>困難模式</button>
                    {/* <button>12</button>
                    <button>12s</button>
                    <input type="text"/>
                    <button>submit</button> */}
                </div>
    }
}

class Card extends React.Component{
    // state = {
    //     click:false
    // }
    // filpCard = () =>{

    // }
    render(){
        return (<div>
                    <span>{this.props.emoji}</span>
                </div>)
    }
}

export default class Game extends React.Component{
    state = {card:[]}

    diffcultGetter = (column) =>{
        let newState = {card:[]}
        for (let i = 0; i < column; i++) {
            newState.card.push({id:nanoid(),emoji:String.fromCodePoint(0x1f601 + i)})
        }
        console.log(newState.card)
        this.setState(newState)
    }

    render(){
        return (<main>
                    <ChoiceDifficult diffcult={this.diffcultGetter}/>
                    <div className="container">{
                        this.state.card.map(card =>{
                            return <Card key={card.id} {...card}/>
                        })}
                        </div>
                </main>)
    }
}
