import React from "react";
// import {nanoid} from 'nanoid'
import './Game.css'

class ChoiceDifficult extends React.Component{
    render(){
        return <div className="ChoiceDifficult">
            <button></button>
            </div>
    }
}

class Item extends React.Component{
    render(){
        return(<li>
                    <label>
                        <input type="checkbox" defaultChecked={this.props.done}/>
                        <span>{this.props.name}</span>
                    </label>
                    <button className="btn btn-danger"> delete</button>
                </li>
        )
    }
}

class List extends React.Component{
    render(){
        return (<ul>{
                this.props.todos.map( todos =>{
                    return <Item key={todos.id} {...todos}/>
                })
            }</ul>
        )
    }
}

class H extends React.Component{
    render(){
        return (
            <div className="to-header">
                <input onKeyUp={this.handle} type="text" placeholder="input and enter" />
            </div>
        )
    }
    handle = (event) => {
        if (event.keyCode != 13) return
        this.props.a({id:'1',name:event.target.value,done:false})
        // console.log(event.target.value)
    }
}

export default class Game extends React.Component{

    state = {todos:[
        {id:'001',name:'lunch',done:true},
        {id:'002',name:'dinner',done:true},
        {id:'003',name:'sleep',done:false}
    ]}

    addToDo = (toDoObj) =>{ 
        const newToDos = [toDoObj,...this.state.todos]
        this.setState({todos:newToDos})
        console.log(this.state)
        // console.log(data)
    }
    render(){
        return (<div>

{/* <button class="button-81" role="button">Button 81</button> */}
                    <H a={this.addToDo}/>
                    <List todos={this.state.todos}/>
                </div>)
    }
}
