import React, { useLayoutEffect } from "react";
import { nanoid } from 'nanoid';
import './Game.css';

class ChoiceDifficult extends React.Component {
    difficultHandle = (event) => {
        let column;
        switch (event.target) {
            case this.easy:
                column = 4;
                break;
            case this.normal:
                column = 6;
                break;
            case this.hard:
                column = 8;
                break;
            default:
                return;
        }
        this.props.diffcult(column);
    }

    render() {
        return (
            <div className="choiceDifficult">
                <button ref={current => this.easy = current} onClick={this.difficultHandle}>簡單模式</button>
                <button ref={current => this.normal = current} onClick={this.difficultHandle}>正常模式</button>
                <button ref={current => this.hard = current} onClick={this.difficultHandle}>困難模式</button>
            </div>
        );
    }
}

class Card extends React.Component {
    render() {
        return (
            <div className='card' onClick={this.props.filpCard}>
                <span>{this.props.match ? this.props.emoji : ''}</span>
            </div>
        );
    }
}

export default class Game extends React.Component {
    state = {
        card: [],
        clickCards: [],
        matchedCards: []
    }

    cardGetter = (card) => {
        const { clickCards, matchedCards } = this.state;

        if (clickCards.length < 2 && !matchedCards.includes(card.id)) {
            this.setState({ clickCards: [...clickCards, card] }, () => {
                if (this.state.clickCards.length === 2) {
                    setTimeout(this.checkCards, 1000);
                }
            });
        }
    }

    checkCards = () => {
        const [firstCard, secondCard] = this.state.clickCards;

        if (firstCard.emoji === secondCard.emoji) {
            this.setState(prevState => ({
                matchedCards: [...prevState.matchedCards, firstCard.id, secondCard.id],
                clickCards: []
            }));
        } else {
            this.setState({ clickCards: [] });
        }
    }

    diffcultGetter = (column) => {
        let newState = { card: [] };
        for (let i = 0; i < column; i++) {
            const emoji = String.fromCodePoint(0x1f601 + i);
            newState.card.push({ id: nanoid(), emoji, match: false });
            newState.card.push({ id: nanoid(), emoji, match: false });
        }
        newState.card.sort(() => Math.random() - 0.5); // Shuffle cards
        this.setState(newState);
    }

    render() {
        return (
            <main>
                <ChoiceDifficult diffcult={this.diffcultGetter} />
                <div className="cards-container">
                    {this.state.card.map(card => (
                        <Card 
                            key={card.id} 
                            cardGetter={() => this.cardGetter(card)} 
                            {...card} 
                            match={this.state.matchedCards.includes(card.id)} 
                        />
                    ))}
                </div>
            </main>
        );
    }
}
