import React from "react";
import './Game.css'

function Choice(){
    return <ul>
        <li></li>
        <li></li>
        <li></li>
    </ul>
}

class Item extends React.Component{
    render(){
        console.log(this)
        return(<li>
                    <label>
                        <input type="checkbox"/>
                        <span>xxxxxx</span>
                    </label>
                    <button className="btn btn-danger"> delete</button>
                </li>
        )
    }
}

class List extends React.Component{
    render(){
        // console.log(this)
        return (<ul>
            {
                this.props.todos.map( todos =>{
                    return <Item key={todos.id} todos={todos}/>
                })
            }
        </ul>
        )
    }
}

class H extends React.Component{
    render(){
        return (
            <div className="to-header">
                <input type="text" placeholder="input and enter" />
            </div>
        )
    }
}

export default class Game extends React.Component{

    state = {todos:[
        {id:'001',name:'lunch',done:true},
        {id:'002',name:'dinner',done:true},
        {id:'003',name:'sleep',done:true}
    ]}
    render(){
        return (<div>
                    <H />
                    <List todos={this.state.todos}/>
                </div>)
    }
}
