import React from "react";
// import {nanoid} from 'nanoid'
import './Game.css'

class ChoiceDifficult extends React.Component{

    diffcultHandle = (event) =>{
        // console.log(event.target.innerText)
        this.props.diffcult(event.target.innerText)
    }

    render(){
        return <div className="choiceDifficult">
                    <button onClick={this.diffcultHandle}>5</button>
                    {/* <button>12</button>
                    <button>12s</button>
                    <input type="text"/>
                    <button>submit</button> */}
                </div>
    }
}

class Card extends React.Component{
    // state = {
    //     click:false
    // }
    // filpCard = () =>{

    // }
    render(){
        return (<div>
                    <span>helo</span>
                </div>)
    }
}

export default class Game extends React.Component{
    state = {card:[
        {id:'001',emoji:'u'}]
    }

    diffcultGetter = (cardObj) =>{
        console.log(cardObj)
        const newCard = [cardObj,...this.state.card]
        this.setState({card:newCard})
    }

    render(){
        return (<main>
            <ChoiceDifficult diffcult={this.diffcultGetter}/>
            <div className="container">{
                this.state.card.map(card =>{
                    return <Card key={card.id} {...card}/>
                })
            }</div>
        </main>)
    }
}

